package assets

import (
	"encoding/base64"
	"pure-kit/engine/internal"
	"pure-kit/engine/storage"
	"pure-kit/engine/window"

	rl "github.com/gen2brain/raylib-go/raylib"
)

const baseTexture = ``
const baseAtlas = ``

func LoadDefaults(texture, atlas bool) {
	if texture {
		var tex = loadTexture(baseTexture)
		internal.Textures["default-texture"] = &tex
	}

	if atlas {
		var retAtlas = loadTexture(baseAtlas)
		internal.Textures["default-atlas"] = &retAtlas
	}
}

// region private

func loadTexture(b64 string) rl.Texture2D {
	if !rl.IsWindowReady() {
		window.Recreate()
	}

	var imgData, _ = base64.StdEncoding.DecodeString(b64)
	var decompressed = storage.Decompress(imgData)
	var image = rl.LoadImageFromMemory(".png", decompressed, int32(len(decompressed)))
	defer rl.UnloadImage(image)

	return rl.LoadTextureFromImage(image)
}
func printBase64(imgPath string) {
	var img = rl.LoadImage(imgPath)
	var bytes = rl.ExportImageToMemory(*img, ".png")
	var compressed = storage.Compress(bytes)
	var b64 = base64.StdEncoding.EncodeToString(compressed)

	print(b64)
}

func Main() {
	// printBase64("default-texture.png")
	printBase64("default-retro-atlas.png")
}

// endregion
